                array_reduce(array_map(fn ($item): bool => ($item) instanceof MonthlyPricePlanStrategyItem, $this->pricing), fn ($carry, $item): bool => $carry && $item, true) => $this->pricing,
            };
        }
        if (isset($this->support)) {
            $this->support = clone $this->support;
        }
    }
}
